// "use strict";
const { UseCaseError } = require("uu_appg01_core-appserver");

class SysAuditLogGeneralError extends UseCaseError {
  static get ERROR_PREFIX() {
    return "uu-appauditlog/";
  }

  constructor(dtoOut, paramMap = {}, cause = null) {
    if (paramMap instanceof Error) {
      cause = paramMap;
      paramMap = {};
    }

    super({ dtoOut, paramMap, cause, status: 400 });
  }
}

module.exports = SysAuditLogGeneralError;
