"use strict";

const UseCaseError = require("./use-case-error.js");

class AuthenticationFailure extends UseCaseError {
  constructor(msg, cause = null) {
    super(msg, cause);
    this.status = 500;
    this.code = `${UseCaseError.ERROR_PREFIX}authentication/unexpectedFailure`;
  }
}

module.exports = AuthenticationFailure;
