"use strict";

const UseCaseError = require("./use-case-error.js");
const DIAGNOSTIC_ERROR_PREFIX = `${UseCaseError.ERROR_PREFIX}server/sys/`;

const Generate = {
  UC_CODE: `${DIAGNOSTIC_ERROR_PREFIX}generateDiagnosticReport/`,
  MissingLibrary: class extends UseCaseError {
    constructor(msg, cause = null) {
      super(msg, cause);
      this.status = 400;
      this.code = `${Generate.UC_CODE}missingLibrary`;
    }
  },
  InvalidConfiguration: class extends UseCaseError {
    constructor(msg, cause = null) {
      super(msg, cause);
      this.status = 400;
      this.code = `${Generate.UC_CODE}invalidConfiguration`;
    }
  }
};

module.exports = {
  Generate
};
