"use strict";

const UseCaseError = require("./use-case-error.js");

class InvalidNode extends UseCaseError {
  constructor(msg, cause = null) {
    super(msg, cause);
    this.status = 400;
    this.code = `${UseCaseError.ERROR_PREFIX}server/targetNodeUnreachable`;
  }
}

module.exports = InvalidNode;
