"use strict";

const UseCaseError = require("./use-case-error.js");

class MethodNotAllowed extends UseCaseError {
  constructor(msg, cause = null) {
    super(msg, cause);
    this.status = 405;
    this.code = `${UseCaseError.ERROR_PREFIX}server/invalidHttpMethod`;
  }
}

module.exports = MethodNotAllowed;
