"use strict";

const UseCaseError = require("./use-case-error.js");

class NotAcceptable extends UseCaseError {
  constructor(msg, cause = null) {
    super(msg, cause);
    this.status = 406;
    this.code = `${UseCaseError.ERROR_PREFIX}server/unsupportedResponseMediaType`;
  }
}

module.exports = NotAcceptable;
