"use strict";

const UseCaseError = require("./use-case-error.js");

class UnsupportedMediaType extends UseCaseError {
  constructor(msg, cause = null) {
    super(msg, cause);
    this.status = 415;
    this.code = `${UseCaseError.ERROR_PREFIX}server/unsupportedRequestMediaType`;
  }
}

module.exports = UnsupportedMediaType;
