"use strict";

const Sys = require("./sys.js");

/**
 * Provides server health status.
 */
class GetHealth {

  static call(ucEnv) {
    Sys.authorize(ucEnv);
    let response = Object.assign({}, Sys.getAppInfo());
    response.serverStatus = "OK";
    response.uuAppErrorMap = {};
    return response;
  }

}

module.exports = GetHealth;
