"use strict";

const Sys = require("./sys.js");
const statistics = require("../monitoring/statistics.js");
const GetStatistics = require("./get-statistics.js");

/**
 * Resets server usage statistics.
 */
class ResetStatistics {

  static call(ucEnv) {
    Sys.authorize(ucEnv);
    statistics.reset();
    return GetStatistics.call(ucEnv);
  }

}

module.exports = ResetStatistics;
