"use strict";

/**
 * This class store all matchers which can be used for validation process.
 */

class MatcherStore {
  constructor() {
    this.matchers = {};
  }

  /**
   * Store matcher to MatcherStore.
   * @param {String} key
   * @param {Function} func
   */
  registerMatcher(key, func) {
    this.matchers[key] = func;
  }
}

module.exports = new MatcherStore();
