"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");
const MatcherError = require("../matcher-error.js");
const Sizeof = require("object-sizeof");

class _Any extends Matcher {

  constructor(...params) {
    super("any", ...params);
  }

  processParams(...params) {
    this.maxSize = params[0];
    if (this.maxSize != null && (typeof this.maxSize !== "number" || this.maxSize < 0)) {
      throw new MatcherError("Any: invalid parameter0, expected Number greater than or equal to 0");
    }
  }

  match(value, ctx) {
    const result = new ValidationResult();

    if (value != null && this.maxSize != null && Sizeof(value) > this.maxSize) {
      result.addInvalidValueError("any.e001", `The size of the any must not exceed ${this.maxSize} B.`);
    }

    return result;
  }

}

function Any(...params) {
  let matcher = new _Any(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Any;
