"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const BASE64_REGEXP = /^(?:[A-Za-z0-9+/]{4})*(?:[A-Za-z0-9+/]{2}==|[A-Za-z0-9+/]{3}=|[A-Za-z0-9+/]{4})$/;

class _Base64 extends Matcher {

  constructor() {
    super("base64");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(BASE64_REGEXP)) {
        result.addInvalidValueError("base64.e001", "The value must be string containing base64 encoded value.");
      }
    } else {
      result.addInvalidTypeError("base64.e001", "The value must be string containing base64 encoded value.");
    }

    return result;
  }

}

function Base64() {
  let matcher = new _Base64();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Base64;
