"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

class _Boolean extends Matcher {

  constructor() {
    super("boolean");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value !== "boolean") {
      result.addInvalidTypeError("boolean.e001", "The value must be boolean.");
    }

    return result;
  }
}

function BooleanMatcher() {
  let matcher = new _Boolean();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = BooleanMatcher;
