"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const CODE_REGEXP = /^[0-9a-zA-Z_]{3,64}$/;

class _Code extends Matcher {

  constructor() {
    super("code");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(CODE_REGEXP)) {
        result.addInvalidValueError("code.e001", "The value must be code.");
      }
    } else {
      result.addInvalidTypeError("code.e001", "The value must be code.");
    }

    return result;
  }

}

function Code() {
  let matcher = new _Code();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Code;
