"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const GPS_REGEXP = /^[-+]?([1-8]?\d\.\d+|90\.0+),\s*[-+]?(180\.0+|((1[0-7]\d)|([1-9]?\d))\.\d+)$/;

class _Gps extends Matcher {

  constructor() {
    super("gps");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(GPS_REGEXP)) {
        result.addInvalidValueError("gps.e001", "The value must match GPS coordinates.");
      }
    } else {
      result.addInvalidTypeError("gps.e001", "The value must match GPS coordinates.");
    }

    return result;
  }

}

function Gps() {
  let matcher = new _Gps();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Gps;
