"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const HEXA32_CODE_REGEXP = /^[0-9a-f]{32}$/;

class _Hexa32Code extends Matcher {

  constructor() {
    super("hexa32Code");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(HEXA32_CODE_REGEXP)) {
        result.addInvalidValueError("hexa32Code.e001", "The value must be 32 characters long hexadecimal code.");
      }
    } else {
      result.addInvalidTypeError("hexa32Code.e001", "The value must be 32 characters long hexadecimal code.");
    }

    return result;
  }

}

function Hexa32Code() {
  let matcher = new _Hexa32Code();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Hexa32Code;
