"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const ID_REGEXP = /^(?=[0-9a-f]+$)(.{24}|.{32})$/;

class _Id extends Matcher {

  constructor() {
    super("id");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(ID_REGEXP)) {
        result.addInvalidValueError("id.e001", "The value must be id.");
      }
    } else {
      if (typeof value !== "object" || !value.constructor || value.constructor.name !== "ObjectID") {
        result.addInvalidTypeError("id.e001", "The value must be id.");
      }
    }

    return result;
  }

}

function Id() {
  let matcher = new _Id();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = Id;
