"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

//https://google.github.io/styleguide/javaguide.html#s5.3-camel-case
const CASE_REGEXP = /^[a-z]+((\d)|([A-Z0-9][a-z0-9]+))*([A-Z])?$/;

class _LowerCamelCase extends Matcher {

  constructor() {
    super("lowerCamelCase");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(CASE_REGEXP)) {
        result.addInvalidValueError("lowerCamelCase.e001", "The value must be lowerCamelCase.");
      }
    } else {
      result.addInvalidTypeError("lowerCamelCase.e001", "The value must be lowerCamelCase.");
    }

    return result;
  }

}

function LowerCamelCase() {
  let matcher = new _LowerCamelCase();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = LowerCamelCase;
