"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const OBJECT_CODE_REGEXP = /^[a-zA-Z0-9_/\\~&-.]{3,128}$/;

class _ObjectCode extends Matcher {

  constructor() {
    super("objectCode");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(OBJECT_CODE_REGEXP)) {
        result.addInvalidValueError("objectCode.e001", "The value must be objectCode.");
      }
    } else {
      result.addInvalidTypeError("objectCode.e001", "The value must be objectCode.");
    }

    return result;
  }

}

function ObjectCode() {
  let matcher = new _ObjectCode();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = ObjectCode;
