"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const SEARCH_KEY_REGEXP = /^.{3,64}$/;

class _SearchKey extends Matcher {

  constructor() {
    super("searchKey");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(SEARCH_KEY_REGEXP)) {
        result.addInvalidValueError("searchKey.e001", "The value must be searchKey.");
      }
    } else {
      result.addInvalidTypeError("searchKey.e001", "The value must be searchKey.");
    }

    return result;
  }

}

function SearchKey() {
  let matcher = new _SearchKey();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = SearchKey;
