"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");
const { Config } = require("uu_appg01_core-utils");

const UU_IDENTITY_DEFAULT_PATTERN = "^[0-9]{1,4}-[0-9]{1,4}(-[0-9]{1,4}(-[0-9]{1,4})?)?$";
const UU_IDENTITY_REGEXP = new RegExp(Config.get("uu_app_validation_uuidentity_pattern") || UU_IDENTITY_DEFAULT_PATTERN);

class _UuIdentity extends Matcher {

  constructor() {
    super("uuIdentity");
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == undefined) {
      return result;
    }

    if (typeof value === "string" || value instanceof String) {
      if (!value.match(UU_IDENTITY_REGEXP)) {
        result.addInvalidValueError("uuIdentity.e001", "The value must be uuIdentity.");
      }
    } else {
      result.addInvalidTypeError("uuIdentity.e001", "The value must be uuIdentity.");
    }

    return result;
  }

}

function UuIdentity() {
  let matcher = new _UuIdentity();
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = UuIdentity;
