/* eslint-disable */
const schemaType = shape({
  realization: string(500).isRequired(),
  maxNoi: integer(),
  maxSoi: integer(),
  maxSob: integer(),
});

const dataStoreType = shape({
  type: oneOf(["uuAppObjectStore", "uuAppBinaryStore"]),
  realization: string(32),
  schemaMap: map(string(/.*/), schemaType()),
});

const persistenceMapType = shape({
  primary: code(),
  maxNoa: integer(1000000),
  uuSubAppDataStore: map(string(/.*/), dataStoreType()),
});
