const path = require("path");
const shell = require("shelljs");

const paths = require("../config/paths.js");
const UuCloudConfig = require("./uu_cloud/uu-cloud-config.js");
const Package = require("uu_appg01_devkit-common/src/tools/package.js");

module.exports = class UuLogStoreExport {

  constructor(config) {
    this.config = config;
    this.taskName = "uuLogStoreExport";
  }

  async process() {
    shell.mkdir("-p", paths.buildDir);
    let {name, version} = Package.getSingletonSync("package.json").get();
    let uuCloudConfig = new UuCloudConfig(path.resolve("."), name, version, paths.buildDir, this.config, this.taskName);

    const Task = require("./uu_cloud/task-logstore-export");
    await new Task(uuCloudConfig).process();
  }

};
