"use strict";

const ObjectStoreError = require("./objectstore-error.js");

class InvalidRevision extends ObjectStoreError {
  constructor(cause, message) {
    super(message || "Revision attribute is not valid. Entered value does not match value in database.", cause);
    this.code = `${ObjectStoreError.ERROR_PREFIX}invalidRevision`;
  }
}

module.exports = InvalidRevision;
