"use strict";

const ObjectStoreError = require("./objectstore-error.js");

class MissingRevision extends ObjectStoreError {
  constructor(cause, message) {
    super(
      message || "Revision attribute is missing. Attribute is mandatory when using revision based strategy.",
      cause
    );
    this.code = `${ObjectStoreError.ERROR_PREFIX}missingRevision`;
  }
}

module.exports = MissingRevision;
