"use strict";

const {UseCaseError} = require("uu_appg01_core-appserver");

class OidcCmdError extends UseCaseError {

  static get ERROR_PREFIX() {
    return "uu-app-oidc/";
  }

  constructor(dtoOut, paramMap = {}, cause = null) {
    if (dtoOut instanceof Error) {
      cause = dtoOut;
      paramMap = {};
      dtoOut = null;
    } else if (paramMap instanceof Error) {
      cause = paramMap;
      paramMap = {};
    }
    super({ dtoOut: dtoOut, paramMap: paramMap, status: 400, cause: cause });
  }

}

module.exports = OidcCmdError;
