"use strict";

const SysAppSecretStoreAbl = require("../../abl/sys-app-secret-store-abl.js");

class UuAppSecretStore {
  /**
   * Stores sysUuAppSecretStore entity.
   *
   * @param {string} awid awid
   * @param {string} secretSet
   * @param {string} code
   * @param {string} secret
   * @returns {Promise<Object>}
   */
  async putSecret(awid, secretSet, code, secret) {
    return await SysAppSecretStoreAbl.putSecret(awid, secretSet, code, secret);
  }

  /**
   * Returns decrypted secret value.
   *
   * @param {string} awid awid
   * @param {string} secretSet
   * @param {string} code
   * @returns {Promise<Object>}
   */
  async getSecret(awid, secretSet, code) {
    return await SysAppSecretStoreAbl.getSecret(awid, secretSet, code);
  }

  /**
   * Returns list secrets for particular secretSet.
   *
   * @param {string} awid awid
   * @param {string} secretSet
   * @returns {Promise<Object>}
   */
  async listSecretCodes(awid, secretSet) {
    return await SysAppSecretStoreAbl.listSecretCodes(awid, secretSet);
  }

  /**
   * Removes sysUuAppSecretStore entity.
   *
   * @param {string} awid awid
   * @param {string} secretSet
   * @param {string} code
   * @returns {Promise<Object>}
   */
  async removeSecret(awid, secretSet, code) {
    return await SysAppSecretStoreAbl.removeSecret(awid, secretSet, code);
  }
}

module.exports = new UuAppSecretStore();
