"use strict";

const SysAppClientTokenAbl = require("../../abl/sys-app-client-token-abl");

class SysAppClientTokenController {
  initKeys(ucEnv) {
    const awid = ucEnv.uri.getAwid();
    return SysAppClientTokenAbl.initKeys(awid);
  }

  listKeys(ucEnv) {
    const awid = ucEnv.uri.getAwid();
    return SysAppClientTokenAbl.listKeys(awid);
  }
}

module.exports = new SysAppClientTokenController();
