"use strict";

const SysDataStoreStatsAbl = require("../../abl/sys-app-data-store-stats-abl.js");

class SysAppDataStoreStatsController {

  get(ucEnv) {
    const awid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysDataStoreStatsAbl.get(awid, dtoIn);
  }

  list(ucEnv) {
    const dtoIn = ucEnv.getDtoIn();
    return SysDataStoreStatsAbl.list(dtoIn);
  }

  calculate(ucEnv) {
    const awid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysDataStoreStatsAbl.calculate(awid, dtoIn);
  }

  privilegedCalculate(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysDataStoreStatsAbl.privilegedCalculate(asid, dtoIn);
  }
}

module.exports = new SysAppDataStoreStatsController();
