"use strict";

const SysAppSecretStoreAbl = require("../../abl/sys-app-secret-store-abl");

class SysAppSecretStoreController {
  rotateMasterKey(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysAppSecretStoreAbl.rotateMasterKey(asid, dtoIn);
  }

  getStatus(ucEnv) {
    return SysAppSecretStoreAbl.getStatus();
  }
}

module.exports = new SysAppSecretStoreController();
