"use strict";

const SysPermissionAbl = require("../../abl/sys-permission-abl.js");

class SysPermissionController {

  createPermission(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysPermissionAbl.create(asid, dtoIn);
  }

  deletePermission(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysPermissionAbl.deletePermission(asid, dtoIn);
  }

  listPermissions(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysPermissionAbl.listPermissions(asid, dtoIn);
  }

}

module.exports = new SysPermissionController();
