"use strict";

const sysProfileAbl = require("../../abl/sys-profile-abl.js");

class SysProfileController {

  setAppProfile(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return sysProfileAbl.setAppProfile(asid, dtoIn);
  }

  getAppProfile(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return sysProfileAbl.getAppProfile(asid, dtoIn);
  }

  listAppProfiles(ucEnv) {
    const asid = ucEnv.uri.getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return sysProfileAbl.listAppProfiles(asid, dtoIn);
  }

  updateProfiles(ucEnv) {
    const dtoIn = ucEnv.getDtoIn();
    return sysProfileAbl.updateProfiles(dtoIn);
  }
}

module.exports = new SysProfileController();
