"use strict";

const SysUpgradeAbl = require("../../abl/sys-upgrade-abl");

class SysUpgradeController {
  upgrade(ucEnv) {
    const session = ucEnv.getSession();
    return SysUpgradeAbl.upgrade(ucEnv.getDtoIn(),session.getIdentity().getUuIdentity());
  }

  upgradeCleanup() {
    return SysUpgradeAbl.upgradeCleanup();
  }
}

module.exports = new SysUpgradeController();
