/* eslint-disable */
const sysRestoreDtoInType = shape({
  strictMode: boolean(),
  awidList: array(hexa32Code(), 1, null),
  data: binary().isRequired()
});

const sysDumpDtoInType = shape({
  awidList: array(hexa32Code(), 1, null)
});

const uuAppDataStoreDumpSchemaMetadataType = shape({
  schemaName: string().isRequired(),
  indexList: array(
    shape({
      name: string().isRequired(),
      key: map(string(), oneOf([1, -1, string()])).isRequired()
    }, true)
  ).isRequired(),
  awidList: array(
    shape({
      awid: hexa32Code().isRequired("asid"),
      asid: hexa32Code(),
      itemCount: any().isRequired(),
      fileIdList: array(mongoId())
    }, true)
  ).isRequired(),
  uuSubAppVersion: string(),
  cts: datetime()
});

const sysAwidRestoreDtoInType = shape({
  strictMode: boolean(),
  data: binary().isRequired()
});

const sysImportDtoInType = shape({
  data: binary().isRequired()
});

const sysImportSchemaMetadataType = shape({
  schemaName: string().isRequired(),
  indexList: array(
    shape({
      name: string().isRequired(),
      key: map(string(), oneOf([1, -1, string()])).isRequired()
    }, true)
  ).isRequired(),
  awidList: array(
    shape({
      awid: hexa32Code().isRequired(),
      itemCount: number().isRequired(),
      fileIdList: array(mongoId())
    }, true),
    1,
    1
  ).isRequired(),
  uuSubAppVersion: string(),
  cts: datetime()
});

const sysListImportMappingsDtoInType = shape({
  origIdList: array(mongoId(), 1, 1000),
  pageInfo: shape({
    pageIndex: integer(),
    pageSize: integer()
  })
});
