"use strict";

const {UuAppKey} = require("uu_appg01_core-authentication");

/**
 * @deprecated
 * Old implementation of uuAppKey, left for backward compatibility.
 * // TODO remove in next major version.
 */
class AppKey extends UuAppKey {

  static parse() {
    try {
      return UuAppKey.parse(...arguments);
    } catch (e) {
      e.code = e.code.replace("uu-app-key/", "uu-app-workspace/sys/appKey/")
      throw e;
    }
  }

  static valueOf() {
    try {
      return UuAppKey.valueOf(...arguments);
    } catch (e) {
      e.code = e.code.replace("uu-app-key/", "uu-app-workspace/sys/appKey/")
      throw e;
    }
  }
}

module.exports = AppKey;
