"use strict";

const {UuObjectDao} = require("uu_appg01_objectstore");

class SysAppInstanceMongo extends UuObjectDao {
  async createSchema() {
    await super.createIndex(
      {
        asid: 1
      },
      {unique: true}
    );
  }

  async create(uuObject, lock) {
    return super.insertOne(uuObject, lock);
  }

  async lockOne(asid, lock) {
    return await super.lockOne({asid}, lock);
  }

  async unlockOne(asid, lock) {
    return await super.unlockOne({asid}, lock);
  }

  async getByAsid(asid) {
    return super.findOne({asid});
  }

  async update(uuObject, lock) {
    return super.findOneAndUpdate({asid: uuObject.asid}, uuObject, "NONE", lock);
  }

  async deleteByAsid(asid) {
    return super.deleteOne({asid});
  }
}

module.exports = SysAppInstanceMongo;
