"use strict";

const { Config } = require("uu_appg01_core-utils");

class AsidErrorHelper {
  constructor() {}

  /**
   * Returns error class based on awid/asid call.
   *
   * @param {String} awid or asid
   * @param {Object} asidErrorClass
   * @param {Object} awidErrorClass
   * @returns {WorkspaceError}
   */
  getErrorClass(awid, asidErrorClass, awidErrorClass) {
    let asid = Config.get("asid");
    if (awid === asid) {
      return asidErrorClass;
    } else {
      return awidErrorClass;
    }
  }
}

module.exports = new AsidErrorHelper();
