"use strict";

const ADVICE_NOTE_SEVERITY_INFO = "info";

class UuCmdHelper {
  static fillAdviceNote(dtoIn, uuIdentity) {
    if (!dtoIn.adviceNote) {
      dtoIn.adviceNote = {}
    }
    dtoIn.adviceNote.uuIdentity = uuIdentity;
    dtoIn.adviceNote.startTime = new Date();

    if (!dtoIn.adviceNote.severity) {
      dtoIn.adviceNote.severity = ADVICE_NOTE_SEVERITY_INFO;
    }

    if (!dtoIn.adviceNote.message) {
      dtoIn.adviceNote.message = null;
    }

    if (!dtoIn.adviceNote.estimatedEndTime) {
      dtoIn.adviceNote.estimatedEndTime = null;
    }
    return dtoIn;
  }
}

module.exports = UuCmdHelper;
