"use strict";

const SysProductInfoAbl = require("../../abl/sys-product-info-abl");

class ProductInfo {
  /**
   * Sets product info of uuSubAppInstance / uuAppWorkspace.
   *
   * @param {string} awid awid or asid to which logo is set
   * @param {Object} dtoIn attributes same as sys/uuAppWorkspace/productInfo/set uuCmd
   * @returns {Promise<Object>} sysProductInfo uuObject
   */
  async set(awid, dtoIn) {
    return await SysProductInfoAbl.setProductInfo(awid, dtoIn);
  }

  /**
   * Returns product information of uuSubAppInstance / uuAppWorkspace.
   *
   * @param {string} awid awid or asid
   * @returns {Promise<Object>} sysProductInfo uuObject
   */
  async get(awid) {
    return await SysProductInfoAbl.getProductInfo(awid);
  }
}

module.exports = new ProductInfo();
