"use strict";

const SysProductLogoAbl = require("../../abl/sys-product-logo-abl");

class ProductLogo {
  /**
   * Sets product logo of uuAppInstance / uuAppWorkspace.
   *
   * @param {string} awid awid or asid to which logo is set
   * @param {Object} dtoIn attributes same as sys/uuAppWorkspace/productLogo/set uuCmd
   * @returns {Promise<Object>} sysProductLogo uuObject
   */
  async set(awid, dtoIn) {
    return await SysProductLogoAbl.setProductLogo(awid, dtoIn);
  }

  /**
   * Returns stored product logo of uuAppInstance / uuAppWorkspace.
   *
   * @param {string} awid awid or asid
   * @param {Object} dtoIn attributes same as sys/uuAppWorkspace/productLogo/get uuCmd
   * @returns {Promise<Stream>} logo
   */
  async get(awid, dtoIn) {
    return await SysProductLogoAbl.getProductLogo(awid, dtoIn);
  }
}

module.exports = new ProductLogo();
