"use strict";

const SysProductInfoAbl = require("../../abl/sys-product-info-abl");

class SysProductInfoController {
  setProductInfo(ucEnv) {
    const awid = ucEnv.getUri().getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysProductInfoAbl.setProductInfo(awid, dtoIn);
  }

  getProductInfo(ucEnv) {
    const awid = ucEnv.getUri().getAwid();
    ucEnv.getResponse().setResourceCache({ maxAge: 60 * 60 * 24, public: false }); // 1 day
    return SysProductInfoAbl.getProductInfo(awid);
  }
}

module.exports = new SysProductInfoController();
