"use strict";

const SysProductLogoAbl = require("../../abl/sys-product-logo-abl");

class SysProductLogoController {
  setProductLogo(ucEnv) {
    const awid = ucEnv.getUri().getAwid();
    const dtoIn = ucEnv.getDtoIn();
    return SysProductLogoAbl.setProductLogo(awid, dtoIn);
  }

  async getProductLogo(ucEnv) {
    const awid = ucEnv.getUri().getAwid();
    const dtoIn = ucEnv.getDtoIn();

    const dtoOut = await SysProductLogoAbl.getProductLogo(awid, dtoIn);
    ucEnv.getResponse().setResourceCache({ maxAge: 60 * 60 * 24 * 5, public: false }); // 5 days

    return ucEnv.setBinaryDtoOut(dtoOut);
  }
}

module.exports = new SysProductLogoController();
