const { UseCaseError } = require("uu_appg01_core-appserver");

class UuApprepresentationError extends UseCaseError {
  static get ERROR_PREFIX() {
    return this.APP_REPRESENTATION_LIB_ERROR_PREFIX;
  }

  static get APP_REPRESENTATION_LIB_ERROR_PREFIX() {
    return "uu-apprepresentation/";
  }

  static get APP_WORKSPACE_ERROR_PREFIX() {
    return this.APP_REPRESENTATION_LIB_ERROR_PREFIX + "sys/uuAppWorkspace/";
  }

  static get APP_INSTANCE_ERROR_PREFIX() {
    return this.APP_REPRESENTATION_LIB_ERROR_PREFIX + "sys/uuSubAppInstance/";
  }

  constructor(dtoOut, paramMap = {}, cause = null) {
    if (paramMap instanceof Error) {
      cause = paramMap;
      paramMap = {};
    }

    super({ dtoOut: dtoOut, paramMap: paramMap, status: 400, cause: cause });
  }
}

module.exports = UuApprepresentationError;
