const { DaoFactory } = require("uu_appg01_objectstore");

class Startup {
  /**
   * Initializing productInfo and productLogo schemas on server start.
   */
  async onStartup() {
    if (DaoFactory.isDataStoreOn()) {
      const productInfoAbl = require("../abl/sys-product-info-abl");
      await productInfoAbl.createSchema();

      const productLogoAbl = require("../abl/sys-product-logo-abl");
      await productLogoAbl.createSchema();
    }
  }
}

module.exports = Startup;
